import React from 'react'


// Mostre os dados da aplicação, como aprensetado no vídeo
// Não utilize CSS externo, use o style para mudar as cores
// Se a situação estiver ativa pinte de verde, inativa vermelho
// Se o gasto for maior que 10000 mostre uma mensagem
const luana = {
  cliente: 'Luana',
  idade: 27,
  compras: [
    { nome: 'Notebook', preco: 'R$ 2500' },
    { nome: 'Geladeira', preco: 'R$ 3000' },
    { nome: 'Smartphone', preco: 'R$ 1500' },
  ],
  ativa: true,
};

const mario = {
  cliente: 'Mario',
  idade: 31,
  compras: [
    { nome: 'Notebook', preco: 'R$ 2500' },
    { nome: 'Geladeira', preco: 'R$ 3000' },
    { nome: 'Smartphone', preco: 'R$ 1500' },
    { nome: 'Guitarra', preco: 'R$ 3500' },
  ],
  ativa: false,
};

const App = () => {
  const dados = mario;
  const soma = dados.compras.reduce((ac, item)=>{
    const preco = +item.preco.replace('R$', '')
    return ac + preco
  }, 0)
  const passouDe10000 = document.createElement('p')
  passouDe10000.innerText = 'Você está gastando muito.'
  return (
  <div>
    <p>Nome: {dados.cliente}</p>
    <p>Idade: {dados.idade}</p>
    <p>Situação: <span style={dados.ativa ? {color: 'green'} : {color: 'red'}}>{dados.ativa ? 'Ativa' : 'Inativa'}</span></p>
    <p>Total gasto: R$ {soma}</p>
    { soma > 10000 ? <p>Você está gastando muito.</p> : null}
  </div>
  );
};


export default App;
